import homeCard from './OOS';
import List from './List';
import { motion } from 'framer-motion';
import background from "./bg.png";
import React from "react";
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';

let card={width: 160, height:100, backgroundColor:"#fff",
 margin:16, 
 borderRadius:12, 
 textAlign:'center', 
 boxShadow: "0px 4px 10px 0px rgba(65, 95, 255, 0.25)",
 opacity: 0.8,
 color: "#666"
}
// color: "#415FFF"

const cardHover={
  scale: 1.1,
  boxShadow: "0px 0px 12px 0px rgba(65, 95, 255, 0.6)",
  fontWeight: 'bold',
  color: "#415fff",
  opacity: 1
}

function App() {

 return (
<Router> 
<Switch>
  <Route extact path="/">



  <motion.div> 
  <homeCard />
  <motion.h1  style={{padding:30}}
initial={{
   marginLeft: 30, 
   y: -20
   }}
animate={{
  fontSize: 60, 
  color: "#333",
 // color: "#415FFF",
 marginLeft: 60,  
   y: 0
   }}
  transition={{ type:"spring", velocity: 120 }}  
   >
  OOS动效平台
  </motion.h1>
   <div>
   <motion.div whileHover={cardHover} style={card} ><p style={{paddingTop:40}}>滑动对位</p></motion.div>
   <motion.div whileHover={cardHover} style={card} ><p style={{paddingTop:40}}>滑动对位</p></motion.div>
   <motion.div whileHover={cardHover} style={card} ><p style={{paddingTop:40}}>子标题 </p></motion.div>

   </div>
   <div style={{marginLeft: 180, marginTop:-348}}>
   <motion.div whileHover={cardHover} style={card} ><p style={{paddingTop:40}}>子页签</p> </motion.div>
   <motion.div whileHover={cardHover} style={card} ><p style={{paddingTop:40}}>滑动及回弹 </p></motion.div>

   </div>
   <div style={{ backgroundImage: `url(${background})` ,width: 375, height:812, position: 'absolute' , top: 0, 
   opacity: 1, 
zIndex: '-1'}}></div>
   </motion.div>

   </Route>
   <Route  path="/List">
     <List />
   </Route>
</Switch>
   </Router>
  );
}

export default App;
